  3â†’// MARK: - Portfolio
  4â†’struct Portfolio: Codable {
  5â†’    var stocks: [Stock]
  6â†’    var lastSynced: Date?
  7â†’    var currency: Currency
  8â†’    var tier: String?
  9â†’
 10â†’    var totalValue: Double {
 11â†’        stocks.reduce(0) { $0 + $1.currentValue }
 12â†’    }
 13â†’
 14â†’    var totalCost: Double {
 15â†’        stocks.reduce(0) { $0 + $1.totalCost }
 16â†’    }
 17â†’
 18â†’    var totalProfitLoss: Double {
 19â†’        totalValue - totalCost
 20â†’    }
 21â†’
 22â†’    var totalProfitLossPercentage: Double {
 23â†’        guard totalCost > 0 else { return 0 }
 24â†’        return (totalProfitLoss / totalCost) * 100
 25â†’    }
 26â†’
 27â†’    var totalDayChange: Double? {
 28â†’        let changes = stocks.compactMap { stock -> Double? in
 29â†’            guard let dayChange = stock.dayChange else { return nil }
 30â†’            return dayChange * stock.totalShares
 31â†’        }
 32â†’        guard !changes.isEmpty else { return nil }
 33â†’        return changes.reduce(0, +)
 34â†’    }
 35â†’
 36â†’    var totalDayChangePercentage: Double? {
 37â†’        guard let dayChange = totalDayChange, totalValue > 0 else { return nil }
 38â†’        let previousValue = totalValue - dayChange
 39â†’        guard previousValue > 0 else { return nil }
 40â†’        return (dayChange / previousValue) * 100
 41â†’    }
 42â†’
 43â†’    init(stocks: [Stock] = [], lastSynced: Date? = nil, currency: Currency = .usd, tier: String? = nil) {
 44â†’        self.stocks = stocks
 45â†’        self.lastSynced = lastSynced
 46â†’        self.currency = currency
 47â†’        self.tier = tier
 48â†’    }
 49â†’
 50â†’    // MARK: - Custom Codable
 51â†’
 52â†’    enum CodingKeys: String, CodingKey {
 53â†’        case stocks, lastSynced, currency, tier
 54â†’    }
 55â†’
 56â†’    init(from decoder: Decoder) throws {
 57â†’        let container = try decoder.container(keyedBy: CodingKeys.self)
 58â†’
 59â†’        // Decode stocks - handle both array and dictionary formats
 60â†’        if let stocksArray = try? container.decode([Stock].self, forKey: .stocks) {
 61â†’            // API returns array format: { "stocks": [...] }
 62â†’            self.stocks = stocksArray
 63â†’        } else if let stocksDict = try? container.decode([String: Stock].self, forKey: .stocks) {
 64â†’            // API returns dictionary format: { "stocks": { "id1": {...}, "id2": {...} } }
 65â†’            self.stocks = Array(stocksDict.values)
 66â†’        } else {
 67â†’            // Fallback to empty array
 68â†’            self.stocks = []
 69â†’        }
 70â†’
 71â†’        self.lastSynced = try container.decodeIfPresent(Date.self, forKey: .lastSynced)
 72â†’        self.currency = (try? container.decode(Currency.self, forKey: .currency)) ?? .usd
 73â†’        self.tier = try container.decodeIfPresent(String.self, forKey: .tier)
 74â†’    }
 75â†’
 76â†’    func encode(to encoder: Encoder) throws {
 77â†’        var container = encoder.container(keyedBy: CodingKeys.self)
 78â†’        try container.encode(stocks, forKey: .stocks)
 79â†’        try container.encodeIfPresent(lastSynced, forKey: .lastSynced)
 80â†’        try container.encode(currency, forKey: .currency)
 81â†’        try container.encodeIfPresent(tier, forKey: .tier)
 82â†’    }
 83â†’}
