 3â†’// MARK: - Double Currency Extensions
 4â†’extension Double {
 5â†’    func toCurrency(currency: Currency = .usd, showSymbol: Bool = true, decimals: Int = 2) -> String {
 6â†’        let formatter = NumberFormatter()
 7â†’        formatter.numberStyle = .decimal
 8â†’        formatter.minimumFractionDigits = decimals
 9â†’        formatter.maximumFractionDigits = decimals
10â†’        formatter.groupingSeparator = ","
11â†’
12â†’        guard let formattedNumber = formatter.string(from: NSNumber(value: self)) else {
13â†’            return "\(currency.symbol)0.00"
14â†’        }
15â†’
16â†’        if showSymbol {
17â†’            return "\(currency.symbol)\(formattedNumber)"
18â†’        } else {
19â†’            return formattedNumber
20â†’        }
21â†’    }
22â†’
23â†’    func toPercentage(decimals: Int = 2, showSign: Bool = true) -> String {
24â†’        let formatter = NumberFormatter()
25â†’        formatter.numberStyle = .decimal
26â†’        formatter.minimumFractionDigits = decimals
27â†’        formatter.maximumFractionDigits = decimals
28â†’
29â†’        guard let formattedNumber = formatter.string(from: NSNumber(value: abs(self))) else {
30â†’            return "0.00%"
31â†’        }
32â†’
33â†’        let sign = showSign && self > 0 ? "+" : ""
34â†’        let negativeSign = self < 0 ? "-" : ""
35â†’
36â†’        return "\(negativeSign)\(sign)\(formattedNumber)%"
37â†’    }
38â†’
39â†’    func toShortCurrency(currency: Currency = .usd) -> String {
40â†’        let absValue = abs(self)
41â†’        let sign = self < 0 ? "-" : ""
42â†’
43â†’        if absValue >= 1_000_000_000 {
44â†’            return "\(sign)\(currency.symbol)\(String(format: "%.1fB", absValue / 1_000_000_000))"
45â†’        } else if absValue >= 1_000_000 {
46â†’            return "\(sign)\(currency.symbol)\(String(format: "%.1fM", absValue / 1_000_000))"
47â†’        } else if absValue >= 1_000 {
48â†’            return "\(sign)\(currency.symbol)\(String(format: "%.1fK", absValue / 1_000))"
49â†’        } else {
50â†’            return toCurrency(currency: currency)
51â†’        }
52â†’    }
53â†’
54â†’    func rounded(toPlaces places: Int) -> Double {
55â†’        let divisor = pow(10.0, Double(places))
56â†’        return (self * divisor).rounded() / divisor
57â†’    }
58â†’}
