61â†’extension Double {
62â†’    func convert(from: Currency, to: Currency, rate: Double) -> Double {
63â†’        if from == to {
64â†’            return self
65â†’        }
66â†’
67â†’        if from == .usd && to == .cad {
68â†’            return self * rate
69â†’        } else if from == .cad && to == .usd {
70â†’            return self / rate
71â†’        }
72â†’
73â†’        return self
74â†’    }
