 28â†’struct EquityLabsApp: App {
 29â†’    @State private var clerk = Clerk.shared
 30â†’    @StateObject private var authService = AuthService.shared
 31â†’    @StateObject private var authManager = AuthManager.shared
 32â†’    @StateObject private var subscriptionManager = SubscriptionManager.shared
 33â†’    @StateObject private var syncManager = PortfolioSyncManager.shared
 34â†’    @Environment(\.scenePhase) private var scenePhase
 35â†’    @AppStorage(Constants.UserDefaultsKeys.appearanceMode) private var appearanceMode: String = AppearanceMode.system.rawValue
 36â†’
