 42â†’    var body: some Scene {
 43â†’        WindowGroup {
 44â†’            Group {
 45â†’                if authManager.isAuthReady && authManager.isAuthenticated {
 46â†’                    // Show dashboard only after auth is fully initialized and API token is set
 47â†’                    DashboardView()
 48â†’                        .environment(\.managedObjectContext, persistenceController.viewContext)
 49â†’                        .environment(\.clerk, clerk)
 50â†’                        .environmentObject(authService)
 51â†’                        .environmentObject(authManager)
 52â†’                        .environmentObject(subscriptionManager)
 53â†’                } else if authManager.isAuthReady {
 54â†’                    // Auth is ready but user is not authenticated
 55â†’                    SignInView()
 56â†’                        .environment(\.clerk, clerk)
 57â†’                        .environmentObject(authService)
 58â†’                        .environmentObject(authManager)
 59â†’                } else {
