 86â†’                }
 87â†’            }
 88â†’            .preferredColorScheme(AppearanceMode(rawValue: appearanceMode)?.colorScheme)
 89â†’            .onChange(of: scenePhase) { _, newPhase in
 90â†’                if newPhase == .active && authManager.isAuthenticated {
 91â†’                    Task {
