 22â†’    /// Configure Clerk with publishable key
 23â†’    func configure() async throws {
 24â†’        guard let publishableKey = Constants.Clerk.publishableKey else {
 25â†’            throw AuthServiceError.missingPublishableKey
 26â†’        }
 27â†’
 28â†’        clerk.configure(publishableKey: publishableKey)
 29â†’
 30â†’        do {
 31â†’            try await clerk.load()
 32â†’            isInitialized = true
 33â†’            isAuthenticated = (clerk.user != nil)
 34â†’
 35â†’            // Save session token to keychain if user is authenticated
 36â†’            if clerk.user != nil, let userId = getClerkUserId() {
 37â†’                try await saveSessionToken(userId: userId)
 38â†’            }
 39â†’
 40â†’            AppLogger.authentication.info("Clerk initialized successfully")
 41â†’        } catch {
 42â†’            AppLogger.authentication.error("Clerk initialization failed: \(error.localizedDescription)")
 43â†’            throw AuthServiceError.initializationFailed(error)
 44â†’        }
