 47â†’    // MARK: - User Info Helpers
 48â†’
 49â†’    func getClerkUserId() -> String? {
 50â†’        guard let user = clerk.user else { return nil }
 51â†’        return Mirror(reflecting: user).children.first(where: { $0.label == "id" })?.value as? String
 52â†’    }
 53â†’
 54â†’    func getUserEmail() -> String? {
 55â†’        guard let user = clerk.user else { return nil }
 56â†’        if let emailAddresses = Mirror(reflecting: user).children.first(where: { $0.label == "emailAddresses" })?.value as? [Any],
 57â†’           let firstEmail = emailAddresses.first {
 58â†’            return Mirror(reflecting: firstEmail).children.first(where: { $0.label == "emailAddress" })?.value as? String
 59â†’        }
 60â†’        return nil
 61â†’    }
 62â†’
 63â†’    func getUserName() -> (firstName: String?, lastName: String?) {
 64â†’        guard let user = clerk.user else { return (nil, nil) }
 65â†’        let mirror = Mirror(reflecting: user)
 66â†’        let firstName = mirror.children.first(where: { $0.label == "firstName" })?.value as? String
 67â†’        let lastName = mirror.children.first(where: { $0.label == "lastName" })?.value as? String
 68â†’        return (firstName, lastName)
 69â†’    }
