 72â†’
 73â†’    /// Get current session token (JWT)
 74â†’    func getSessionToken() async throws -> String {
 75â†’        guard let session = clerk.session else {
 76â†’            throw AuthServiceError.noActiveSession
 77â†’        }
 78â†’
 79â†’        guard let token = try await session.getToken() else {
 80â†’            throw AuthServiceError.tokenRetrievalFailed
 81â†’        }
 82â†’
 83â†’        return token.jwt
 84â†’    }
 85â†’
 86â†’    /// Save session token to keychain for API client usage
 87â†’    private func saveSessionToken(userId: String) async throws {
 88â†’        do {
 89â†’            let token = try await getSessionToken()
 90â†’            try keychainManager.save(token, forKey: Constants.KeychainKeys.authToken)
 91â†’            try keychainManager.save(userId, forKey: Constants.KeychainKeys.userId)
 92â†’            AppLogger.authentication.debug("Session token saved to keychain")
 93â†’        } catch {
 94â†’            AppLogger.authentication.error("Failed to save session token: \(error.localizedDescription)")
 95â†’            throw error
 96â†’        }
 97â†’    }
