  5â†’// MARK: - SubscriptionManager
  6â†’@MainActor
  7â†’class SubscriptionManager: ObservableObject {
  8â†’    static let shared = SubscriptionManager()
  9â†’
 10â†’    @Published var subscriptionState: SubscriptionState = SubscriptionState()
 11â†’    @Published var product: Product?
 12â†’    @Published var isLoading = false
 13â†’    @Published var error: Error?
 14â†’
 15â†’    private let apiClient = APIClient.shared
 16â†’    private var updateListenerTask: Task<Void, Error>?
 17â†’
 18â†’    private init() {
 19â†’        // Start listening for transaction updates
 20â†’        updateListenerTask = listenForTransactions()
 21â†’        Task { await loadProduct() }
 22â†’    }
 23â†’
 24â†’    deinit {
 25â†’        updateListenerTask?.cancel()
 26â†’    }
 27â†’
 28â†’    // MARK: - Load Product
 29â†’    func loadProduct() async {
 30â†’        do {
 31â†’            let products = try await Product.products(for: [Constants.Subscription.monthlyProductId])
 32â†’            product = products.first
 33â†’            AppLogger.subscription.info("Loaded product: \(product?.displayName ?? "none")")
 34â†’        } catch {
 35â†’            AppLogger.subscription.error("Failed to load products: \(error.localizedDescription)")
 36â†’        }
 37â†’    }
 38â†’
 39â†’    // MARK: - Load Subscription State
 40â†’    func loadSubscriptionState() async {
 41â†’        isLoading = true
 42â†’        defer { isLoading = false }
 43â†’
 44â†’        // Check for active subscriptions
 45â†’        for await result in Transaction.currentEntitlements {
 46â†’            guard case .verified(let transaction) = result else {
 47â†’                continue
 48â†’            }
 49â†’
 50â†’            if transaction.productID == Constants.Subscription.monthlyProductId {
 51â†’                subscriptionState = SubscriptionState(
 52â†’                    tier: .paid,
 53â†’                    productId: transaction.productID,
 54â†’                    purchaseDate: transaction.purchaseDate,
 55â†’                    expirationDate: transaction.expirationDate,
 56â†’                    isActive: true,
 57â†’                    lastValidated: Date()
 58â†’                )
 59â†’
 60â†’                AppLogger.subscription.info("Active subscription found")
 61â†’                return
 62â†’            }
 63â†’        }
 64â†’
 65â†’        // No active subscription found
 66â†’        subscriptionState = SubscriptionState(tier: .free)
 67â†’        AppLogger.subscription.debug("No active subscription")
 68â†’    }
 69â†’
 70â†’    // MARK: - Apply Backend Tier
 71â†’    func applyBackendTier(_ tier: String) {
 72â†’        if tier == "paid" && subscriptionState.tier == .free {
 73â†’            subscriptionState = SubscriptionState(
 74â†’                tier: .paid,
 75â†’                isActive: true,
 76â†’                lastValidated: Date()
 77â†’            )
 78â†’            AppLogger.subscription.info("Applied backend tier: paid")
 79â†’        } else if tier == "free" && subscriptionState.tier == .paid {
 80â†’            subscriptionState = SubscriptionState(tier: .free)
 81â†’            AppLogger.subscription.info("Applied backend tier: free")
 82â†’        }
 83â†’    }
 84â†’
 85â†’    // MARK: - Check Stock Limit
 86â†’    func canAddStock(currentCount: Int) -> Bool {
 87â†’        guard let maxStocks = subscriptionState.tier.maxStocks else {
 88â†’            return true // Unlimited
 89â†’        }
 90â†’        return currentCount < maxStocks
 91â†’    }
 92â†’
 93â†’    func stocksRemaining(currentCount: Int) -> Int? {
 94â†’        guard let maxStocks = subscriptionState.tier.maxStocks else {
 95â†’            return nil // Unlimited
 96â†’        }
 97â†’        return max(0, maxStocks - currentCount)
 98â†’    }
 99â†’
100â†’    // MARK: - Transaction Listener
101â†’    private func listenForTransactions() -> Task<Void, Error> {
102â†’        return Task.detached {
103â†’            for await result in Transaction.updates {
104â†’                guard case .verified(let transaction) = result else {
105â†’                    continue
106â†’                }
107â†’
108â†’                await transaction.finish()
109â†’
110â†’                await MainActor.run {
111â†’                    Task {
112â†’                        await self.loadSubscriptionState()
113â†’                    }
114â†’                }
115â†’            }
116â†’        }
117â†’    }
118â†’
119â†’    // MARK: - Purchase
120â†’    func purchase() async throws {
121â†’        guard let product else {
122â†’            throw SubscriptionError.productNotLoaded
123â†’        }
124â†’
125â†’        isLoading = true
126â†’        defer { isLoading = false }
127â†’
128â†’        let result = try await product.purchase()
129â†’
130â†’        switch result {
131â†’        case .success(let verification):
132â†’            guard case .verified(let transaction) = verification else {
133â†’                throw SubscriptionError.purchaseFailed
134â†’            }
135â†’
136â†’            await transaction.finish()
137â†’
138â†’            // Send App Store receipt to backend for validation
139â†’            await sendReceiptToBackend(productId: transaction.productID)
140â†’
141â†’            // Reload subscription state
142â†’            await loadSubscriptionState()
143â†’
144â†’            AppLogger.subscription.info("Purchase successful")
145â†’
146â†’        case .userCancelled:
147â†’            AppLogger.subscription.debug("Purchase cancelled by user")
148â†’
149â†’        case .pending:
150â†’            AppLogger.subscription.info("Purchase pending approval")

