import SwiftUI

// MARK: - DashboardView
struct DashboardView: View {
    @EnvironmentObject var authManager: AuthManager
    @EnvironmentObject var subscriptionManager: SubscriptionManager
    @StateObject private var viewModel = DashboardViewModel()
    @StateObject private var settingsViewModel = SettingsViewModel()
    @State private var showSubscription = false

    var body: some View {
        NavigationStack {
            ZStack {
                if viewModel.isLoading {
                    LoadingView(message: "Loading portfolio...")
                } else if let error = viewModel.error {
                    ErrorView(error: error) {
                        Task {
                            await viewModel.loadPortfolio()
                        }
                    }
                } else if viewModel.stocks.isEmpty {
                    EmptyStateView(
                        icon: "chart.line.uptrend.xyaxis",
                        title: "No Stocks Yet",
                        message: "Start tracking your investments by adding your first stock",
                        actionTitle: "Add Stock"
                    ) {
                        viewModel.showAddStock = true
                    }
                } else {
                    portfolioContent
                }
            }
            .navigationTitle("EquityLabs")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button {
                        if canAddStock {
                            viewModel.showAddStock = true
                        } else {
                            showSubscription = true
                        }
                    } label: {
                        Image(systemName: "plus.circle.fill")
                            .font(.title2)
                    }
                }

                ToolbarItem(placement: .navigationBarLeading) {
                    Button {
                        viewModel.showSettings = true
                    } label: {
                        Image(systemName: "gearshape.fill")
                            .font(.title3)
                    }
                }
            }
            .sheet(isPresented: $viewModel.showAddStock) {
                AddStockView()
            }
            .sheet(isPresented: $viewModel.showSettings, onDismiss: {
                Task {
                    await viewModel.reloadPreferences()
                    await settingsViewModel.awaitPendingSync()
                    await viewModel.loadPortfolio()
                }
            }) {
                SettingsView(viewModel: settingsViewModel)
            }
            .sheet(isPresented: $showSubscription) {
                SubscriptionView()
            }
            .task {
                if viewModel.stocks.isEmpty {
                    await viewModel.loadPortfolio()
                }
            }
        }
    }

    private var portfolioContent: some View {
        ScrollView {
            VStack(spacing: 20) {
                // Portfolio summary
                PortfolioSummaryView(summary: viewModel.summary)
                    .padding(.horizontal)

                // Stock list
                LazyVStack(spacing: 12) {
                    ForEach(viewModel.sortedStocks) { stock in
                        NavigationLink(destination: StockDetailView(stock: stock)) {
                            StockCardView(stock: stock)
                        }
                        .buttonStyle(PlainButtonStyle())
                    }
                }
                .padding(.horizontal)
            }
            .padding(.vertical)
        }
        .refreshable {
            await viewModel.refreshPrices()
        }
    }

    private var canAddStock: Bool {
        subscriptionManager.canAddStock(currentCount: viewModel.stocks.count)
    }
}

// MARK: - Preview
#Preview {
    DashboardView()
        .environmentObject(AuthManager.shared)
        .environmentObject(SubscriptionManager.shared)
        .environment(\.managedObjectContext, PersistenceController.preview.viewContext)
}

import SwiftUI

// MARK: - StockCardView
struct StockCardView: View {
    let stock: Stock

    var body: some View {
        HStack(alignment: .top, spacing: 12) {
            // Symbol circle
            ZStack {
                Circle()
                    .fill(Color.blue.opacity(0.2))
                    .frame(width: 50, height: 50)

                Text(stock.symbol.prefix(2))
                    .font(.headline.bold())
                    .foregroundColor(.blue)
            }

            // Stock info
            VStack(alignment: .leading, spacing: 4) {
                Text(stock.symbol)
                    .font(.headline)
                    .foregroundColor(.textPrimary)

                Text(stock.name)
                    .font(.subheadline)
                    .foregroundColor(.textSecondary)
                    .lineLimit(1)

                HStack(spacing: 4) {
                    Text("\(stock.totalShares, specifier: "%.2f") shares")
                        .font(.caption)
                        .foregroundColor(.textTertiary)

                    Text("â€¢")
                        .foregroundColor(.textTertiary)

                    Text("Avg \(stock.averageCost.toCurrency())")
                        .font(.caption)
                        .foregroundColor(.textTertiary)
                }
            }

            Spacer()

            // Price info
            VStack(alignment: .trailing, spacing: 4) {
                if let price = stock.currentPrice {
                    Text(price.toCurrency(currency: Currency(rawValue: stock.currency) ?? .usd))
                        .font(.headline)
                        .foregroundColor(.textPrimary)
                } else {
                    Text("--")
                        .font(.headline)
                        .foregroundColor(.textSecondary)
                }

                if let dayChange = stock.dayChange,
                   let dayChangePercent = stock.dayChangePercentage {
                    HStack(spacing: 4) {
                        Image(systemName: dayChange >= 0 ? "arrow.up.right" : "arrow.down.right")
                            .font(.caption2)

                        Text(dayChangePercent.toPercentage(decimals: 2, showSign: false))
                            .font(.caption)
                    }
                    .foregroundColor(Color.profitLoss(value: dayChange))
                }

                // P/L
                HStack(spacing: 4) {
                    Text(stock.profitLoss.toCurrency(currency: Currency(rawValue: stock.currency) ?? .usd, showSymbol: false))
                    Text("(\(stock.profitLossPercentage.toPercentage(showSign: false)))")
                }
                .font(.caption2)
                .foregroundColor(Color.profitLoss(value: stock.profitLoss))
            }
        }
        .padding()
        .background(Color.backgroundSecondary)
        .cornerRadius(Constants.Layout.cornerRadius)
    }
}

// MARK: - Preview
#Preview {
    VStack(spacing: 12) {
        StockCardView(stock: Stock(
            symbol: "AAPL",
            name: "Apple Inc.",
            lots: [StockLot(shares: 10, pricePerShare: 140)],
            currentPrice: 150,
            previousClose: 148
        ))

        StockCardView(stock: Stock(
            symbol: "GOOGL",
            name: "Alphabet Inc.",
            lots: [StockLot(shares: 5, pricePerShare: 2800)],
            currentPrice: 2750,
            previousClose: 2760
        ))
    }
    .padding()
    .background(Color.backgroundPrimary)
}

import SwiftUI

// MARK: - PortfolioSummaryView
struct PortfolioSummaryView: View {
    let summary: PortfolioSummary?

    var body: some View {
        VStack(spacing: 16) {
            // Total value
            VStack(spacing: 4) {
                Text("Total Value (\(summary?.currency.rawValue ?? "USD"))")
                    .font(.subheadline)
                    .foregroundColor(.textSecondary)

                Text(summary?.totalValue.toCurrency(currency: summary?.currency ?? .usd) ?? "$0.00")
                    .font(.system(size: 36, weight: .bold))
                    .foregroundColor(.textPrimary)

                if let dayChange = summary?.totalDayChange,
                   let dayChangePercent = summary?.totalDayChangePercentage {
                    HStack(spacing: 4) {
                        Image(systemName: dayChange >= 0 ? "arrow.up" : "arrow.down")
                        Text("\(dayChange.toCurrency(currency: summary?.currency ?? .usd)) (\(dayChangePercent.toPercentage()))")
                    }
                    .font(.subheadline)
                    .foregroundColor(Color.profitLoss(value: dayChange))
                }
            }

            Divider()

            // Summary stats
            HStack(spacing: 32) {
                StatView(
                    title: "Cost",
                    value: summary?.totalCost.toCurrency(currency: summary?.currency ?? .usd) ?? "$0.00",
                    color: .textSecondary
                )

                Divider()
                    .frame(height: 40)

                StatView(
                    title: "P/L",
                    value: summary?.totalProfitLoss.toCurrency(currency: summary?.currency ?? .usd) ?? "$0.00",
                    valueColor: summary != nil ? Color.profitLoss(value: summary!.totalProfitLoss) : .textPrimary,
                    subtitle: summary?.totalProfitLossPercentage.toPercentage(),
                    color: .textSecondary
                )

                Divider()
                    .frame(height: 40)

                StatView(
                    title: "Stocks",
                    value: "\(summary?.stockCount ?? 0)",
                    color: .textSecondary
                )
            }
        }
        .glassCardStyle()
    }
}

// MARK: - StatView
private struct StatView: View {
    let title: String
    let value: String
    var valueColor: Color = .textPrimary
    var subtitle: String? = nil
    let color: Color

    var body: some View {
        VStack(spacing: 4) {
            Text(title)
                .font(.caption)
                .foregroundColor(color)

            Text(value)
                .font(.headline)
                .foregroundColor(valueColor)

            if let subtitle = subtitle {
                Text(subtitle)
                    .font(.caption2)
                    .foregroundColor(valueColor)
            }
        }
        .frame(maxWidth: .infinity)
    }
}

// MARK: - Preview
#Preview {
    let portfolio = Portfolio(stocks: [
        Stock(symbol: "AAPL", name: "Apple Inc.", lots: [
            StockLot(shares: 10, pricePerShare: 140)
        ], currentPrice: 150, previousClose: 148)
    ])
    let summary = PortfolioSummary(portfolio: portfolio)

    PortfolioSummaryView(summary: summary)
        .padding()
        .background(Color.backgroundPrimary)
}

