// MARK: - NewsArticleCard
struct NewsArticleCard: View {
    let article: NewsArticle
    var showSentiment: Bool = true

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            // Header: Source and Date
            HStack {
                Text(article.source)
                    .font(.caption)
                    .foregroundColor(.textSecondary)

                Spacer()

                Text(article.publishedAt, style: .relative)
                    .font(.caption)
                    .foregroundColor(.textTertiary)
            }

            // Title
            Text(article.title)
                .font(.headline)
                .lineLimit(3)

            // Summary (if available)
            if let summary = article.summary {
                Text(summary)
                    .font(.subheadline)
                    .foregroundColor(.textSecondary)
                    .lineLimit(2)
            }

            // Sentiment (if available and allowed)
            if showSentiment, let sentiment = article.sentiment {
                HStack(spacing: 4) {
                    Text(sentiment.label.emoji)
                    Text(sentiment.label.displayName)
                        .font(.caption)
                        .foregroundColor(sentimentColor(for: sentiment.label))
                }
                .padding(.horizontal, 8)
                .padding(.vertical, 4)
                .background(sentimentColor(for: sentiment.label).opacity(0.1))
                .cornerRadius(8)
            }

            // Link
            Link(destination: URL(string: article.url)!) {
                HStack {
                    Text("Read More")
                        .font(.subheadline)
                    Image(systemName: "arrow.up.right")
                        .font(.caption)
                }
            }
        }
        .padding()
        .background(Color(.secondarySystemBackground))
        .cornerRadius(12)
    }

    private func sentimentColor(for label: SentimentLabel) -> Color {
        switch label {
        case .positive:
            return .green
        case .neutral:
            return .gray
        case .negative:
            return .red
        }
    }
}

