import SwiftUI

// MARK: - StockDetailView
struct StockDetailView: View {
    @StateObject private var viewModel: StockDetailViewModel
    @Environment(\.dismiss) private var dismiss
    @State private var showSubscription = false
    @Namespace private var tabNamespace
    @ScaledMetric(relativeTo: .largeTitle) private var priceSize: CGFloat = 36

    init(stock: Stock) {
        _viewModel = StateObject(wrappedValue: StockDetailViewModel(stock: stock))
    }

    var body: some View {
        ScrollView {
            VStack(spacing: 0) {
                // Header - Price Info
                priceHeader

                // Tab Selector
                tabSelector

                // Tab Content
                tabContent
            }
        }
        .refreshable {
            await viewModel.loadData()
        }
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .principal) {
                VStack(spacing: 2) {
                    Text(viewModel.stock.symbol)
                        .font(.headline)
                    Text(viewModel.stock.name)
                        .font(.caption)
                        .foregroundColor(.textSecondary)
                }
            }

            ToolbarItem(placement: .primaryAction) {
                if viewModel.isLoading {
                    ProgressView()
                        .transition(.opacity)
                } else {
                    Button {
                        Task {
                            await viewModel.refreshPrice()
                        }
                    } label: {
                        Image(systemName: "arrow.clockwise")
                    }
                    .transition(.opacity)
                }
            }
        }
        .task {
            await viewModel.loadData()
        }
        .alert("Error", isPresented: .constant(viewModel.error != nil), presenting: viewModel.error) { _ in
            Button("OK") {
                viewModel.error = nil
            }
        } message: { error in
            Text(error.localizedDescription)
        }
        .sheet(isPresented: $showSubscription) {
            SubscriptionView()
        }
    }

    // MARK: - Price Header

    private var priceHeader: some View {
        VStack(spacing: 16) {
            // Current Price
            HStack(alignment: .firstTextBaseline, spacing: 8) {
                Text(viewModel.formatPrice(viewModel.currentPrice))
                    .font(.system(size: priceSize, weight: .bold))
                    .minimumScaleFactor(0.6)

                if let change = viewModel.priceChange,
                   let changePercent = viewModel.priceChangePercent {
                    VStack(alignment: .leading, spacing: 2) {
                        Text(viewModel.formatPrice(change))
                            .font(.subheadline)
                            .foregroundColor(change >= 0 ? .green : .red)

                        Text(viewModel.formatPercent(changePercent))
                            .font(.caption)
                            .foregroundColor(change >= 0 ? .green : .red)
                    }
                }
            }

            // Portfolio Summary
            HStack(spacing: 20) {
                // Total Value
                VStack(alignment: .leading, spacing: 4) {
                    Text("Total Value")
                        .font(.caption)
                        .foregroundColor(.textSecondary)
                    Text(viewModel.formatPrice(viewModel.totalValue))
                        .font(.headline)
                }

                Spacer()

                // Gain/Loss
                VStack(alignment: .trailing, spacing: 4) {
                    Text("Total Gain/Loss")
                        .font(.caption)
                        .foregroundColor(.textSecondary)

                    HStack(spacing: 8) {
                        Text(viewModel.formatPrice(viewModel.gainLoss))
                            .font(.headline)
                            .foregroundColor(viewModel.gainLoss >= 0 ? .green : .red)

                        Text(viewModel.formatPercent(viewModel.gainLossPercent))
                            .font(.subheadline)
                            .foregroundColor(viewModel.gainLoss >= 0 ? .green : .red)
                    }
                }
            }
            .padding(.horizontal)

            Divider()
        }
        .padding(.vertical)
        .accessibilityElement(children: .combine)
        .accessibilityLabel(priceHeaderAccessibilityLabel)
    }

    private var priceHeaderAccessibilityLabel: String {
        var label = "\(viewModel.stock.symbol), current price \(viewModel.formatPrice(viewModel.currentPrice))"
        if let change = viewModel.priceChange, let percent = viewModel.priceChangePercent {
            let direction = change >= 0 ? "up" : "down"
            label += ", \(direction) \(viewModel.formatPrice(abs(change))), \(viewModel.formatPercent(abs(percent)))"
        }
        label += ", total value \(viewModel.formatPrice(viewModel.totalValue))"
        let plDirection = viewModel.gainLoss >= 0 ? "gain" : "loss"
        label += ", \(plDirection) \(viewModel.formatPrice(abs(viewModel.gainLoss)))"
        return label
    }

    // MARK: - Tab Selector

    private var tabSelector: some View {
        GlassEffectContainer {
            HStack(spacing: 8) {
                ForEach(DetailTab.allCases, id: \.self) { tab in
                    Button {
                        HapticManager.selection()
                        withAnimation(.smooth) {
                            viewModel.selectedTab = tab
                        }
                    } label: {
                        Text(tab.rawValue)
                            .font(.subheadline)
                            .fontWeight(viewModel.selectedTab == tab ? .semibold : .regular)
                            .foregroundColor(viewModel.selectedTab == tab ? .primary : .textSecondary)
                            .padding(.horizontal, 20)
                            .padding(.vertical, 10)
                            .glassEffect(viewModel.selectedTab == tab ? .regular.interactive() : .clear.interactive(), in: Capsule())
                            .glassEffectID(tab.rawValue, in: tabNamespace)
                    }
                }
            }
            .padding(.vertical, 4)
        }
    }

    // MARK: - Tab Content

    @ViewBuilder
    private var tabContent: some View {
        Group {
            switch viewModel.selectedTab {
            case .overview:
                overviewTab
            case .lots:
                lotsTab
            case .news:
                newsTab
            }
        }
        .gesture(
            DragGesture(minimumDistance: 50, coordinateSpace: .local)
                .onEnded { value in
                    // Only trigger for mostly-horizontal swipes
                    guard abs(value.translation.width) > abs(value.translation.height) else { return }
                    let tabs = DetailTab.allCases
                    guard let currentIndex = tabs.firstIndex(of: viewModel.selectedTab) else { return }
                    if value.translation.width < 0, currentIndex < tabs.count - 1 {
                        withAnimation(.smooth) {
                            viewModel.selectedTab = tabs[currentIndex + 1]
                        }
                    } else if value.translation.width > 0, currentIndex > 0 {
                        withAnimation(.smooth) {
                            viewModel.selectedTab = tabs[currentIndex - 1]
                        }
                    }
                }
        )
    }

    // MARK: - Overview Tab

    private var overviewTab: some View {
        VStack(spacing: 20) {
            // Chart
            StockChartView(
                data: viewModel.historicalData,
                lots: viewModel.stock.lots,
                selectedRange: viewModel.selectedTimeRange,
                isLoading: viewModel.isLoading || viewModel.isLoadingHistory,
                onRangeChange: { range in
                    Task {
                        await viewModel.changeTimeRange(range)
                    }
                }
            )

            // Stats Grid
            statsGrid

            Spacer()
        }
        .padding(.top)
    }

    // MARK: - Stats Grid

    private var statsGrid: some View {
        LazyVGrid(columns: [
            GridItem(.flexible()),
            GridItem(.flexible())
        ], spacing: 16) {
            // Total Shares
            StatCardView(
                title: "Total Shares",
                value: viewModel.formatShares(viewModel.totalShares),
                icon: "chart.bar.fill",
                color: .blue
            )

            // Average Cost
            StatCardView(
                title: "Average Cost",
                value: viewModel.formatPrice(viewModel.averageCost),
                icon: "dollarsign.circle.fill",
                color: .orange
            )

            // Total Cost
            StatCardView(
                title: "Total Cost",
                value: viewModel.formatPrice(viewModel.totalCost),
                icon: "banknote.fill",
                color: .purple
            )

            // Number of Lots
            StatCardView(
                title: "Lots",
                value: "\(viewModel.stock.lots.count)",
                icon: "square.stack.3d.up.fill",
                color: .green
            )
        }
        .padding(.horizontal)
    }

    // MARK: - Lots Tab

    private var lotsTab: some View {
        StockLotsListView(
            stock: viewModel.stock,
            onAddLot: { lot in
                Task {
                    await viewModel.addLot(lot)
                }
            },
            onUpdateLot: { lot in
                Task {
                    await viewModel.updateLot(lot)
                }
            },
            onDeleteLot: { lot in
                Task {
                    await viewModel.deleteLot(lot)
                }
            }
        )
    }

    // MARK: - News Tab

    private var newsTab: some View {
        VStack(spacing: 0) {
            if viewModel.isLoadingNews {
                ProgressView()
                    .padding()
            } else if viewModel.newsArticles.isEmpty {
                emptyNewsView
            } else {
                newsListView
            }
        }
    }

    private var emptyNewsView: some View {
        VStack(spacing: 12) {
            Image(systemName: "newspaper")
                .font(.system(size: 50))
                .foregroundColor(.textSecondary)

            Text("No News Available")
                .font(.headline)
                .foregroundColor(.textSecondary)

            Text("Check back later for updates")
                .font(.subheadline)
                .foregroundColor(.textTertiary)
        }
        .padding()
        .frame(maxWidth: .infinity, maxHeight: .infinity)
    }

    private var newsListView: some View {
        LazyVStack(spacing: 12) {
            if !viewModel.canViewSentiment {
                sentimentUpgradeBanner
            }

            ForEach(viewModel.newsArticles) { article in
                NewsArticleCard(article: article, showSentiment: viewModel.canViewSentiment)
            }
        }
        .padding()
    }

    private var sentimentUpgradeBanner: some View {
        Button {
            showSubscription = true
        } label: {
            HStack(spacing: 12) {
                Image(systemName: "lock.fill")
                    .foregroundColor(.orange)

                VStack(alignment: .leading, spacing: 2) {
                    Text("Upgrade for Sentiment Analysis")
                        .font(.subheadline)
                        .fontWeight(.semibold)
                    Text("Get AI-powered sentiment insights on news articles")
                        .font(.caption)
                        .foregroundColor(.textSecondary)
                }

                Spacer()

                Image(systemName: "chevron.right")
                    .font(.caption)
                    .foregroundColor(.textSecondary)
            }
            .padding()
            .background(Color.orange.opacity(0.1))
            .cornerRadius(12)
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// MARK: - StatCardView
struct StatCardView: View {
    let title: String
    let value: String
    let icon: String
    let color: Color

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            HStack {
                Image(systemName: icon)
                    .foregroundColor(color)
                Text(title)
                    .font(.caption)
                    .foregroundColor(.textSecondary)
                Spacer()
            }

            Text(value)
                .font(.title3)
