                   let changePercent = viewModel.priceChangePercent {
                    VStack(alignment: .leading, spacing: 2) {
                        Text(viewModel.formatPrice(change))
                            .font(.subheadline)
                            .foregroundColor(change >= 0 ? .green : .red)

                        Text(viewModel.formatPercent(changePercent))
                            .font(.caption)
                            .foregroundColor(change >= 0 ? .green : .red)
                    }
                }
