import SwiftUI
import Charts

// MARK: - StockChartView
struct StockChartView: View {
    let data: [HistoricalDataPoint]
    let lots: [StockLot]
    let selectedRange: TimeRange
    let isLoading: Bool
    let onRangeChange: (TimeRange) -> Void

    @State private var selectedPoint: HistoricalDataPoint?
    @Namespace private var rangeNamespace

    var body: some View {
        VStack(spacing: 16) {
            // Chart
            if isLoading && data.isEmpty {
                loadingChartView
            } else if data.isEmpty {
                emptyChartView
            } else {
                chartView
            }

            // Time Range Selector
            timeRangeSelector
        }
        .padding()
    }

    // MARK: - Chart View

    private var chartView: some View {
        VStack(alignment: .leading, spacing: 8) {
            // Selected Point Info
            if let point = selectedPoint {
                selectedPointInfo(point)
            }

            // Chart
            Chart {
                // Price Line
                ForEach(data) { point in
                    LineMark(
                        x: .value("Date", point.date),
                        y: .value("Price", point.close)
                    )
                    .foregroundStyle(chartColor)
                    .interpolationMethod(.catmullRom)

                    // Area Fill
                    AreaMark(
                        x: .value("Date", point.date),
                        y: .value("Price", point.close)
                    )
                    .foregroundStyle(
                        LinearGradient(
                            colors: [chartColor.opacity(0.3), chartColor.opacity(0.0)],
                            startPoint: .top,
                            endPoint: .bottom
                        )
                    )
                    .interpolationMethod(.catmullRom)
                }

                // Lot Price Indicators (horizontal lines)
                ForEach(lots) { lot in
                    RuleMark(y: .value("Lot Price", lot.pricePerShare))
                        .foregroundStyle(.orange.opacity(0.5))
                        .lineStyle(StrokeStyle(lineWidth: 1, dash: [5, 5]))
                        .annotation(position: .trailing, alignment: .center) {
                            Text("$\(lot.pricePerShare, specifier: "%.2f")")
                                .font(.caption2)
                                .foregroundColor(.orange)
                                .padding(.horizontal, 4)
                                .padding(.vertical, 2)
                                .background(.orange.opacity(0.1))
                                .cornerRadius(4)
                        }
                }

                // Average Cost Line
                if !lots.isEmpty {
                    let avgCost = lots.reduce(0) { $0 + ($1.shares * $1.pricePerShare) } /
                                  lots.reduce(0) { $0 + $1.shares }

                    RuleMark(y: .value("Average Cost", avgCost))
                        .foregroundStyle(.blue.opacity(0.7))
                        .lineStyle(StrokeStyle(lineWidth: 2, dash: [10, 5]))
                        .annotation(position: .trailing, alignment: .center) {
                            Text("Avg: $\(avgCost, specifier: "%.2f")")
                                .font(.caption)
                                .fontWeight(.semibold)
                                .foregroundColor(.blue)
                                .padding(.horizontal, 6)
                                .padding(.vertical, 3)
                                .background(.blue.opacity(0.1))
                                .cornerRadius(6)
                        }
                }

                // Selection Indicator
                if let point = selectedPoint {
                    RuleMark(x: .value("Selected", point.date))
                        .foregroundStyle(.gray.opacity(0.3))
                        .lineStyle(StrokeStyle(lineWidth: 1))

                    PointMark(
                        x: .value("Date", point.date),
                        y: .value("Price", point.close)
                    )
                    .foregroundStyle(chartColor)
                    .symbolSize(100)
                }
            }
            .frame(height: 300)
            .chartXAxis {
                AxisMarks(values: .stride(by: xAxisStride)) { _ in
                    AxisValueLabel(format: xAxisFormat)
                    AxisGridLine()
                }
            }
            .chartYAxis {
                AxisMarks(position: .trailing) { value in
                    AxisValueLabel {
                        if let price = value.as(Double.self) {
                            Text("$\(price, specifier: "%.2f")")
                        }
                    }
                    AxisGridLine()
